INSERT INTO `courses` (`Id`, `Subject`, `CourseNo`, `SectionNo`, `Title`, `Faculty`, `Credits`, `InstructorFullName`, `PartOfTerm`, `Corequisite`, `Prerequisite`, `Description`, `ScheduleForPrint`) VALUES
(1404, 'GARM', '103', 'A', 'İş Güvenliği ve Hijyen', 'Uygulamalı Bilimler Fakültesi', 6.00, 'ASLI ZULUĞ', 'UG Tam', '', '', 'Bu ders, yiyecek ve içecek endüstrisinde hijyenin tanımı ve önemi üzerine odaklanmaktadır. Ders gıda güvenliği, iş sağlığı ve güvenliği için uygulamalar ve teknik çözümlerin önemini vurgular.', 'Pazartesi | 13:40 - 16:30'),
(1405, 'GARM', '121', 'A', 'Gıda Bilimi', 'Uygulamalı Bilimler Fakültesi', 6.00, 'SUZAN TİREKİ', 'UG Tam', '', '', 'Bu ders karbonhidratlar, proteinler ve yağlar gibi gıdanın temel bileşenlerini ele alan genel bir gıda bilimine giriş dersidir. Aynı zamanda tahıllar, sebzeler ve meyveler, et, tavuk, balık ve kuru baklagiller, yumurta, katı ve sıvı yağ ürünleri gibi konuları da kapsar.', 'Çarşamba | 13:40 - 15:30Cuma | 09:40 - 11:30'),
(1406, 'GARM', '204', 'A', 'Beslenme', 'Uygulamalı Bilimler Fakültesi', 5.00, 'GİZEM AĞIR', 'UG Tam', '', '', 'Bu ders beslenmenin temellerini, temel besin öğelerini, enerji hesaplamalarını ve besin öğelerinin kaynaklarını kapsar. Profesyonel gastronomi dünyasında sağlıklı gıda seçimleri, pişirme teknikleri ve bunların vejetaryenlik, veganlık, glütensiz beslenme gibi tercih edilen diyetlerde uygun alternatifleri ele alır. ', 'Cuma | 08:40 - 11:30'),
(1407, 'GARM', '221', 'A', 'Klasik Pastacılık Teknik ve Becerilerinin Temelleri', 'Uygulamalı Bilimler Fakültesi', 6.00, 'DEFNE ERTAN TÜYSÜZOĞLU', 'UG Tam', '', 'GARM 103', 'Bu ders, hamurlar, kremalar ve dolgular gibi klasik Fransız pastacılığının temel tekniklerini kapsamaktadır. Pastacılık becerileri ve bilgilerinin üzerine inşa edileceği güçlü bir temel sağlar.', 'Pazartesi | 08:40 - 11:30Pazartesi | 11:40 - 15:30Salı | 11:40 - 15:30Salı | 15:40 - 18:30'),
(1408, 'GARM', '221', 'B', 'Klasik Pastacılık Teknik ve Becerilerinin Temelleri', 'Uygulamalı Bilimler Fakültesi', 6.00, 'DEFNE ERTAN TÜYSÜZOĞLU', 'UG Tam', '', 'GARM 103', 'Bu ders, hamurlar, kremalar ve dolgular gibi klasik Fransız pastacılığının temel tekniklerini kapsamaktadır. Pastacılık becerileri ve bilgilerinin üzerine inşa edileceği güçlü bir temel sağlar.', 'Pazartesi | 08:40 - 11:30Pazartesi | 15:40 - 18:30Salı | 11:40 - 15:30Çarşamba | 08:40 - 11:30'),
(1409, 'GARM', '221', 'C', 'Klasik Pastacılık Teknik ve Becerilerinin Temelleri', 'Uygulamalı Bilimler Fakültesi', 6.00, 'DEFNE ERTAN TÜYSÜZOĞLU', 'UG Tam', '', 'GARM 103', 'Bu ders, hamurlar, kremalar ve dolgular gibi klasik Fransız pastacılığının temel tekniklerini kapsamaktadır. Pastacılık becerileri ve bilgilerinin üzerine inşa edileceği güçlü bir temel sağlar.', 'Pazartesi | 08:40 - 11:30Salı | 08:40 - 11:30Salı | 11:40 - 15:30Çarşamba | 08:40 - 11:30'),
(1410, 'GARM', '221', 'D', 'Klasik Pastacılık Teknik ve Becerilerinin Temelleri', 'Uygulamalı Bilimler Fakültesi', 6.00, 'DEFNE ERTAN TÜYSÜZOĞLU', 'UG Tam', '', 'GARM 103', 'Bu ders, hamurlar, kremalar ve dolgular gibi klasik Fransız pastacılığının temel tekniklerini kapsamaktadır. Pastacılık becerileri ve bilgilerinin üzerine inşa edileceği güçlü bir temel sağlar.', 'Pazartesi | 08:40 - 11:30Salı | 11:40 - 15:30Çarşamba | 11:40 - 14:30Perşembe | 08:40 - 11:30'),
(1411, 'GARM', '222', 'A', 'Uluslararası Mutfaklar', 'Uygulamalı Bilimler Fakültesi', 5.00, 'ÖZGE SAMANCI', 'UG Tam', 'SAS 203', 'GARM 124 ve GARM 221', 'Bu ders, temel gıda hazırlama ve mise en place, mutfak organizasyonu ve yapısı, uygulamalı pratik içeren gerçek bir restoran ortamında reçete yorumlamayı kapsar. Her hafta farklı bir dünya mutfağının pratik uygulaması kültürel çerçeve ile verilir. Her pratik ders, standart porsiyon boyutları, porsiyon kontrol yöntemleri ve standart reçetelerin kullanımına ilişkin ayrıntıları içermektedir.', 'Perşembe | 08:40 - 14:30'),
(1412, 'GARM', '223', 'B', 'Şarap ve İçecek Yönetimi', 'Uygulamalı Bilimler Fakültesi', 5.00, 'AYSELİ İZMEN', 'UG Tam', '', '', 'Ders genel olarak şarap üretimi, üzüm çeşitleri, şarap ve yiyecek eşleştirme, şarap için kalite değerlendirmesine temel bir bakış içerir. Dünyadaki şarap bölgeleri ile birlikte Türkiye\'nin şarap bölgeleri ve Türk şaraplarının özellikleri gözden geçirilir. Şarap tadım teknikleri ve şarap menüsü formülasyonunu kapsar.', 'Çarşamba | 15:40 - 18:30'),
(1413, 'GARM', '224', 'A', 'Gıda ve Sürdürülebilirlik', 'Uygulamalı Bilimler Fakültesi', 5.00, 'CANDAN TÜRKKAN', 'UG Tam', '', '60 ECTS', 'Ders, çağdaş küresel gıda üretim ve tüketim kalıplarının ve uygulamalarının çevre üzerindeki etkilerini, çağdaş gıda rejiminin evrimini ve buna daha sürdürülebilir alternatifler bulmak için dünyadaki çeşitli hareketleri tanıtır. Gıda egemenliği ve gıda güvenliği, Yeşil Devrim, genetiği değiştirilmiş tohumlar, alternatif gıda ağları ve Slow Food hareketi gibi gelişmeleri kapsar.', 'Pazartesi | 08:40 - 11:30'),
(1414, 'GARM', '321', 'A', 'Geleneksel Mutfak Teknik ve Becerilerinin Bölgesel Uygulamaları', 'Uygulamalı Bilimler Fakültesi', 8.00, 'SUZAN TİREKİ', 'UG Tam', '', 'GARM 124', 'Bu ders, ağırlıklı olarak Avrupa yöresel mutfaklarının, özellikle de Fransız yöresel mutfaklarının mutfak tekniklerini kapsamaktadır. Orta ve ileri düzeyde teknik ve sunum becerilerinin uygulandığı mutfak uygulamalarını içermektedir.', 'Pazartesi | 11:40 - 15:30Pazartesi | 15:40 - 18:30Salı | 08:40 - 11:30Salı | 18:40 - 22:30Çarşamba | 08:40 - 11:30Cuma | 15:40 - 19:30'),
